package io.github.soccyuwu.pregxxy.casting.mishapsimport at.petrak.hexcasting.api.casting.eval.CastingEnvironmentimport at.petrak.hexcasting.api.casting.iota.Iotaimport at.petrak.hexcasting.api.casting.mishaps.Mishapimport at.petrak.hexcasting.api.pigment.FrozenPigmentimport at.petrak.hexcasting.api.utils.aquaimport net.minecraft.world.entity.Entityimport net.minecraft.world.item.DyeColorclass MishapCantBreed(private val entity: Entity, private val isGreater: Boolean) : Mishap() {    override fun accentColor(ctx: CastingEnvironment, errorCtx: Context): FrozenPigment =        dyeColor(DyeColor.BROWN)    override fun execute(env: CastingEnvironment, errorCtx: Context, stack: MutableList<Iota>) {        env.mishapEnvironment.yeetHeldItemsTowards(entity.position())    }    override fun errorMessage(ctx: CastingEnvironment, errorCtx: Context) =        error((if(!isGreater) "cantBreed" else "cantBreedStrong").toString(), entity.displayName.plainCopy().aqua)}